exports.trimEnemy = function(enemy){
  var trimmed = {id: '', score: -1};
  trimmed.id = enemy.playerData.id;
  trimmed.score = enemy.score;
  return trimmed;
}

exports.trimRoomForJoinRoom = function(room){
  var trimmed = {
    number: room.number,
    name: 'room' + room.number
  }
  return trimmed;
}


